import * as React from 'react';
import { Formik, Field } from 'formik';
import * as Yup from 'yup';
import {Form,Button,Container} from 'react-bootstrap';
import './styles/Register.css';

class Register extends React.Component{

    constructor(){
        super();
        this.state={
            user:{
                username:"",
                email:"",
                password:"",
                passConfirmation:""
            }
        }
    }

     valideFields = ()=>{
        return Yup.object().shape()({
            username:Yup.string()
                         .required('required'),
            email :Yup.email("enter a valide email")
                       .required("required"),
            password:Yup.min(6,"password needs to be at least 6 charachters")
                        .required('required'),
            passConfirmation:Yup.min(6)
                                .required("required")
        });
    }

    render(){
        return (
            <div id='register-form'>

             <Formik
                initialValues={this.state}
                validate={this.valideFields}
                onSubmit={(values, actions) => {
                    setTimeout(() => {
                    alert(JSON.stringify(values, null, 2));
                    actions.setSubmitting(false);
                    }, 1000);
                }}
                render={props => (
                    <Container>
                    <div className="form-register__holder">
                    <Form>
                    <Form.Group controlId="formBasicEmail">
                      <Form.Label>Username</Form.Label>
                      <Field name="user.username" >
                        {(props)=>{
                            return <Form.Control type="text" placeholder="username" />
                        }}
                      </Field>

                    </Form.Group>
                    <Form.Group controlId="formBasicEmail">
                      <Form.Label>Email address</Form.Label>
                      <Field name="user.email" >
                        {(props)=>{
                            return <Form.Control type="email" placeholder="email" />
                        }}
                      </Field>

                    </Form.Group>
                    <Form.Group controlId="formBasicEmail">
                      <Form.Label>password</Form.Label>
                      <Field name="user.username" >
                        {(props)=>{
                            return <Form.Control type="password" placeholder="password" />
                        }}
                      </Field>

                    </Form.Group>
                    <Form.Group controlId="formBasicEmail">
                      <Form.Label>password confirmation</Form.Label>
                      <Field name="user.username" >
                        {(props)=>{
                            return <Form.Control type="email" placeholder="username" />
                        }}
                      </Field>

                    </Form.Group>
                    <Button variant="primary" type="submit">
                      Register
                    </Button>
                  </Form>
                  </div>
                  </Container>
                )}
                />
            </div>

        );
    }


}


export default Register;
