import * as React from 'react';
import {Link} from 'react-router-dom';
import './styles/homepage.css';
import {Button,Container} from 'react-bootstrap';
import websocket from 'socket.io-client';
import * as axios from 'axios';

class HomePage extends React.Component{

    constructor(){
        super();
        this.state={
            socket:{},
            messageContent:"",
            posts:[]
        }
    }

    handlChnage=(e)=>{
        this.setState({messageContent:e.target.value});
        // broadcasting the message the  current user is typing 
        this.state.socket.emit("typing",{
            username:localStorage.username,
            message:e.target.value
        })
    }
    logout=()=>{
        // clearing all the data in the localstorage
        localStorage.clear();
    }
    componentDidMount(){
       const socket = websocket("http://localhost:8080");
       this.setState({socket})
        socket.on("create",(data)=>{
        if(data.action==="create"){
            this.displayPost(data.post);
        }
    })
    socket.on("typing",data=>{
        console.log(data);
    })
       this.getPosts();
    }

    getPosts=()=>{
       axios
        .get("/post/getAll")
        .then(response=>{
            const posts = response.data.posts;
            this.setState({
                posts
            })
        })
        .catch(error=>{
            alert(error.message);
        })
    }

    displayPost=(post)=>{
        const posts = this.state.posts;
        posts.push(post);
        this.setState({
            posts
        })
    }

    addPost=(post)=>{
        const body = {
            content:this.state.messageContent
        }
         let instance=axios.create({
            headers: {
              post: {     
                token:localStorage.getItem("token")
              }
            }
          });

        instance.post("/post/create",body)
       .then(response=>{
         console.log("response is ",response);
       })
       .catch(error=>{
           alert(error.message);
       })
    }

    editMessage=(messsageId)=>{
        console.log("edit");
    }

    render(){
        return (
            <div id="homepage-wrapper">
            <Container>
            <header className="top-bar__menu">

                    <div className="homepage-wall">
                        <h3>The Wall</h3>
                    </div>
                    <div className="login-form__container">
                    {!localStorage.username?
                        <Link to={"/login"}>
                        <Button 
                            onClick={this.handleClick} 
                            variant="secondary"
                            className="homepage-button__slbtn"
                            >
                            Login/SignUp
                            </Button>
                        </Link>
                        :
                        <Link to={"/"} onClick={this.logout}>
                            <Button 
                                onClick={this.handleClick} 
                                variant="secondary"
                                className="homepage-button__slbtn"
                                >
                                Logout
                                </Button>
                        </Link>
                    }

                    </div>

                </header>

                <div className="posts-container__wrapper">
                <ul className="post-list__item">

                    {this.state.posts.map(post=>{
                        return <li className="post-item">
                            <div className="post-item__sender">
                                {post.username}
                            </div>
                            <div className="post-item__container">
                                <div className="post-item__content">
                                    {post.content}
                                </div> 
                            <div className="post-item__options">
                                {localStorage.username?
                                    <Button 
                                    className="edit-post__btn"
                                    variant="secondary"
                                    onClick={(e)=>{this.editMessage(post.id)}}>edit</Button>
                                :
                                null
                                }
                                </div>
                            </div>
                        </li> 
                    })}
                </ul>
                </div>

                {localStorage.username
                ?
                     <div className="chat-box__area">
                     <textarea
                     className="mesage-field__input"
                     value={this.state.messageContent}
                     placeholder="enter a message"
                     onChange={this.handlChnage}
                     draggable="false"
                     >
                     </textarea>
                 <div className="send-message__button"
                      onClick={this.addPost}
                 >  
                     send
                 </div>
                 </div>
                 :null 
                }

                </Container>
            </div>

        );
    }


}


export default HomePage;
